package graph;import biologicalElements.GraphElementAbstract;import biologicalElements.Pathway;import graph.jung.classes.MyGraph;import gui.MainWindow;public class GraphInstance {    private static final MainWindow w = MainWindow.getInstance();    private static final GraphContainer con = GraphContainer.getInstance();    public static MyGraph getMyGraph() {        try {            return con.getPathway(w.getCurrentPathway()).getGraph();        } catch (Exception e) {            return null;        }    }    public static Pathway getPathwayStatic() {        try {            return con.getPathway(w.getCurrentPathway());        } catch (Exception e) {            return null;        }    }    public Pathway getPathway() {        return con.getPathway(w.getCurrentPathway());    }    public GraphContainer getContainer() {        return con;    }    public GraphElementAbstract getSelectedObject() {        return con.getSelectedObject();    }    public void setSelectedObject(GraphElementAbstract selectedObject) {        con.setSelectedObject(selectedObject);    }}